import React from "react";
import { connect } from "react-redux";

import "./checkout-item.styles.scss";
import {
  removeItemFromCart,
  addItem,
  removeItem,
} from "../../redux/cart/cart.actions";

const CheckoutItem = ({ item, removeItemFromCart, addItem, removeItem }) => (
  <div className="checkout-item">
    <div className="image-container">
      <img src={item.imageUrl} alt="item" />
    </div>
    <span className="name">{item.name}</span>
    <span className="quantity">
      <div
        className="arrow"
        onClick={() =>
          item.quantity > 1 ? removeItem(item) : removeItemFromCart(item)
        }
      >
        &#10094;
      </div>
      {item.quantity}
      <div className="arrow" onClick={() => addItem(item)}>
        &#10095;
      </div>
    </span>
    <span className="price">{item.price}</span>
    <span className="remove-button" onClick={() => removeItemFromCart(item)}>
      &#10005;
    </span>
  </div>
);

const mapDispatchToProps = (dispach) => ({
  removeItemFromCart: (item) => dispach(removeItemFromCart(item)),
  addItem: (item) => dispach(addItem(item)),
  removeItem: (item) => dispach(removeItem(item)),
});

export default connect(null, mapDispatchToProps)(CheckoutItem);
