import React from "react";
import "./menu-item.styles.scss";

const MenuItem = ({ item }) => (
  <div
    style={{
      backgroundImage: `url(${item.imageUrl})`,
    }}
    className="menu-item"
  >
    <div className="content">
      <h1 className="title">{item.title}</h1>
      <span className="subtitle">SHOP NOW</span>
    </div>
  </div>
);

export default MenuItem;
