import styled from "styled-components";
import CollectionItemDivContainer from "./../../components/collection-item/collection-item.styles";

export const CollectionPageDivContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

export const TitleH2Container = styled.h2`
  font-size: 38px;
  margin: 0 auto 30px;
`;

export const ItemsDivContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr;
  grid-gap: 10px;

  & > CollectionItemDivContainer {
    margin-bottom: 30px;
  }
`;

export default CollectionPageDivContainer;
