import styled from "styled-components";

export const SignUpDiv = styled.div`
  width: 30vw;
  display: flex;
  flex-direction: column;
`;

export const TitleH2 = styled.h2`
  margin: 10px 0;
`;

export const ErrorMessage = styled.h2`
  margin: 10px 0;
  text-align: center;
  color: red;
`;
