import React from "react";
import { connect } from "react-redux";
import { addCartItem } from "./../../redux/cart/cart.actions";

import {
  ImageDivContainer,
  AddButtonContainer,
  CollectionItemDivContainer,
  CollectionFooterDivContainer,
  NameSpanContainer,
  PriceSpanContainer,
} from "./collection-item.styles";

const CollectionItem = ({ item, addCartItem }) => {
  const { imageUrl, name, price } = item;
  return (
    <CollectionItemDivContainer>
      <ImageDivContainer />
      <CollectionFooterDivContainer>
        <NameSpanContainer>{name}</NameSpanContainer>
        <PriceSpanContainer>{price}</PriceSpanContainer>
      </CollectionFooterDivContainer>
      <AddButtonContainer inverted onClick={() => addCartItem(item)}>
        Add to cart
      </AddButtonContainer>
    </CollectionItemDivContainer>
  );
};

const mapDispatchToProps = (dispach) => ({
  addCartItem: (item) => dispach(addCartItem(item)),
});

export default connect(null, mapDispatchToProps)(CollectionItem);
